INSERT INTO `topics` VALUES ('91', 'Lịch sử Thế giới', null, '4', 'Lịch sử thế giới, lịch sử toàn cầu hoặc lịch sử xuyên quốc gia (không nên nhầm lẫn với lịch sử ngoại giao hoặc quốc tế) là một lĩnh vực nghiên cứu lịch sử nổi lên như là một lĩnh vực học', '', 'lich-su-the-gioi_1514775382.jpeg', '', '', null, null, '0', 'lich-su-the-gioi');
INSERT INTO `topics` VALUES ('92', 'Chiến tranh Thế giới thứ II', null, '16', 'Chiến tranh thế giới lần thứ hai (WWII hay WW2), còn được gọi là Thế chiến thứ hai, là một cuộc chiến tranh toàn cầu kéo dài từ năm 1939 đến năm 1945, mặc dù xung đột bắt đầu sớm hơn. Nó liên quan', '', 'chien-tranh-the-gioi-thu-ii_1514775615.jpeg', '', '', null, null, '0', 'chien-tranh-the-gioi-thu-ii');
INSERT INTO `topics` VALUES ('93', 'Triết học', null, '4', '\r\nNghiên cứu các vấn đề cơ bản như sự tồn tại, và bản chất của thế giới, thông qua các bài diễn thuyết có tính hệ thống và quan trọng.', '', 'triet-hoc_1514775677.jpeg', '', '', null, null, '0', 'triet-hoc');
INSERT INTO `topics` VALUES ('94', 'Triết học Khoa học', null, '4', '\r\nTriết học của Khoa học liên quan đến các giả định, cơ sở, phương pháp, và hàm ý của khoa học, và với việc sử dụng và công đức của khoa học.', '', 'triet-hoc-khoa-hoc_1514775761.jpeg', '', '', null, null, '0', 'triet-hoc-khoa-hoc');
INSERT INTO `topics` VALUES ('95', 'Sách', null, '5', 'Cuốn sách là một bộ sưu tập gồm các tờ giấy, in, minh hoạ hoặc trống, được làm bằng giấy, vải da, hoặc các vật liệu khác, thường được buộc chặt lại với nhau để làm bản lề một bên.', '', 'sach_1514775904.jpeg', '', '', null, null, '0', 'sach');
INSERT INTO `topics` VALUES ('96', 'Viết', null, '5', '', 'Viết là đại diện của ngôn ngữ trong một môi trường bao gồm việc sử dụng một tập hợp các dấu hiệu hoặc biểu tượng (được gọi là hệ thống viết). Nó được phân biệt với minh họa, chẳng hạn như các bản vẽ hang động và các bức tranh, và bảo quản ngôn ngữ không mang tính biểu tượng thông qua các phương tiện không phải là văn bản, chẳng hạn như các bản ghi âm.\r\n\r\nViết có lẽ bắt nguồn từ kết quả của việc mở rộng chính trị trong các nền văn hoá cổ đại, cần có các phương tiện đáng tin cậy để truyền tải thông tin, duy trì các tài khoản tài chính, lưu giữ hồ sơ lịch sử và các hoạt động tương tự.', 'viet_1514775977.jpeg', '', '', null, null, '0', 'viet');
