INSERT INTO `topics` VALUES ('98', 'Sách Khuyến nghị', null, '5', '', '\r\nYêu cầu đề xuất về sách, ví dụ: Những cuốn sách hay nhất về những người vẽ tranh minh họa là gì?\r\nCâu hỏi thường gặp về đề xuất sách là gì?\r\n\"Bạn trở thành những gì bạn đọc.\"\r\n\"Cho dù bạn bận thế nào đi chăng nữa, bạn vẫn phải tìm thời gian để đọc, hoặc đầu hàng mình vào sự thiếu hiểu biết tự chọn.\" - Confucius', 'sach-khuyen-nghi_1514776246.jpeg', '', '', null, null, '0', 'sach-khuyen-nghi');
