INSERT INTO `topics` VALUES ('104', 'Khoa học', null, '6', 'Hoạt động trí tuệ và thực tiễn bao gồm nghiên cứu có hệ thống về cấu trúc và hành vi của thế giới vật chất và tự nhiên.\r\n- Chủ đề trung tâm về khoa học tự nhiên và tự nhiên, ví dụ: Hóa học', 'Hoạt động trí tuệ và thực tiễn bao gồm nghiên cứu có hệ thống về cấu trúc và hành vi của thế giới vật chất và tự nhiên.\r\n- Chủ đề trung tâm về khoa học tự nhiên và tự nhiên, ví dụ: Hóa học, Sinh học, và Vật lý. Ngoài ra xem Khoa học Xã hội.\r\n- Các câu hỏi liên quan đến các chủ đề Khoa học và Tôn giáo cần được đánh dấu một cách có liên quan hơn theo chủ đề \'Khoa học và Tôn giáo\'.', 'khoa-hoc_1514776688.jpeg', '', '', null, null, '0', 'khoa-hoc');
INSERT INTO `topics` VALUES ('105', 'Khoa học Máy tính', null, '6', 'Khoa học Máy tính là phương pháp khoa học để tính toán.\r\nCác câu hỏi về lập trình nên được thêm vào chương trình máy tính. Các câu hỏi về giáo dục hoặc học về khoa học máy tính nên được đặt tr', 'Khoa học Máy tính là phương pháp khoa học để tính toán.\r\nCác câu hỏi về lập trình nên được thêm vào chương trình máy tính. Các câu hỏi về giáo dục hoặc học về khoa học máy tính nên được đặt trong Giáo dục Khoa học Máy tính hoặc Học về Khoa học Máy tính. Các câu hỏi về công việc hoặc sự nghiệp trong khoa học máy tính nên được đặt trong Jobs and Careers in Computer Science.', 'khoa-hoc-may-tinh_1514776791.jpeg', '', '', null, null, '0', 'khoa-hoc-may-tinh');
INSERT INTO `topics` VALUES ('106', 'Vật lý', null, '6', 'Nghiên cứu vật chất và chuyển động của nó qua không gian và thời gian, cũng như nghiên cứu một số chủ đề liên quan của nó - năng lượng và vũ lực.', 'Nghiên cứu vật chất và chuyển động của nó qua không gian và thời gian, cũng như nghiên cứu một số chủ đề liên quan của nó - năng lượng và vũ lực.', 'vat-ly_1514776845.jpeg', '', '', null, null, '0', 'vat-ly');
INSERT INTO `topics` VALUES ('107', 'Tâm lý học', null, '6', 'Tâm lý học là nghiên cứu tâm trí và hành vi. Đó là một kỷ luật học thuật và một khoa học ứng dụng nhằm tìm hiểu các cá nhân và các nhóm bằng cách thiết lập các nguyên tắc chung và nghiên cứu các', 'Tâm lý học là nghiên cứu tâm trí và hành vi. Đó là một kỷ luật học thuật và một khoa học ứng dụng nhằm tìm hiểu các cá nhân và các nhóm bằng cách thiết lập các nguyên tắc chung và nghiên cứu các trường hợp cụ thể. Trong lĩnh vực này, một chuyên gia hoặc nhà nghiên cứu được gọi là nhà tâm lý học và có thể được phân loại là một nhà khoa học xã hội, hành vi hoặc nhận thức. Các nhà tâm lý học cố gắng hiểu được vai trò của các chức năng tâm thần trong hành vi cá nhân và xã hội, đồng thời khám phá các quá trình sinh lý và sinh học làm cơ sở cho các chức năng nhận thức và hành vi.\r\n', 'tam-ly-hoc_1514776955.jpeg', '', '', null, null, '0', 'tam-ly-hoc');
INSERT INTO `topics` VALUES ('108', 'Kinh tế', null, '6', 'Khoa học xã hội liên quan đến việc mô tả và phân tích sản xuất, phân phối và tiêu thụ hàng hoá và dịch vụ.\r\nBao gồm lý thuyết kinh tế, nguyên tắc và thực tiễn, cũng như điều kiện kinh tế.', 'Khoa học xã hội liên quan đến việc mô tả và phân tích sản xuất, phân phối và tiêu thụ hàng hoá và dịch vụ.\r\nBao gồm lý thuyết kinh tế, nguyên tắc và thực tiễn, cũng như điều kiện kinh tế.', 'kinh-te_1514777050.jpeg', '', '', null, null, '0', 'kinh-te');
INSERT INTO `topics` VALUES ('109', 'Toán học', null, '6', 'Nghiên cứu về số lượng, hình dạng, sự thay đổi, và không gian thông qua việc khấu trừ và suy luận nghiêm ngặt.', 'Nghiên cứu về số lượng, hình dạng, sự thay đổi, và không gian thông qua việc khấu trừ và suy luận nghiêm ngặt.', 'toan-hoc_1514777142.jpeg', '', '', null, null, '0', 'toan-hoc');
