INSERT INTO `topics` VALUES ('111', 'Lập trình Máy tính', null, '6', 'Lập trình máy tính (thường được rút ngắn thành lập trình) là một quá trình dẫn từ một sự hình thành ban đầu của một vấn đề máy tính đến các chương trình thực thi. Nó bao gồm các hoạt động n', 'Lập trình máy tính (thường được rút ngắn thành lập trình) là một quá trình dẫn từ một sự hình thành ban đầu của một vấn đề máy tính đến các chương trình thực thi. Nó bao gồm các hoạt động như phân tích, hiểu biết, và giải quyết chung các vấn đề như vậy dẫn đến một thuật toán, xác minh các yêu cầu của thuật toán bao gồm tính chính xác của nó và tiêu thụ tài nguyên của nó, thực hiện (thường được gọi là mã hóa) của thuật toán trong một ngôn ngữ lập trình mục tiêu, thử nghiệm, gỡ lỗi và duy trì mã nguồn, thực hiện xây dựng hệ thống và quản lý các sản phẩm có nguồn gốc, chẳng hạn như mã máy của các chương trình máy tính.\r\n', 'lap-trinh-may-tinh_1514777279.jpeg', '', '', null, null, '0', 'lap-trinh-may-tinh');
INSERT INTO `topics` VALUES ('112', 'Kỹ thuật Phần mềm', null, '6', 'Kỹ thuật Phần mềm (SE) là một nghề chuyên dụng để thiết kế, triển khai, sửa đổi và xác nhận phần mềm để nó có chất lượng cao hơn, giá cả phải chăng hơn, có thể duy trì và nhanh hơn để xây d', 'Kỹ thuật Phần mềm (SE) là một nghề chuyên dụng để thiết kế, triển khai, sửa đổi và xác nhận phần mềm để nó có chất lượng cao hơn, giá cả phải chăng hơn, có thể duy trì và nhanh hơn để xây dựng.\r\n\"Mô phỏng của thế giới thực với thế giới số\"', 'ky-thuat-phan-mem_1514777350.jpeg', '', '', null, null, '0', 'ky-thuat-phan-mem');
INSERT INTO `topics` VALUES ('113', 'Trí tuệ nhân tạo', null, '16', 'Học máy là nghiên cứu các thuật toán máy tính cải tiến tự động thông qua kinh nghiệm và là trung tâm của các nghiên cứu về AI từ khi bắt đầu thực địa.', 'Học máy là nghiên cứu các thuật toán máy tính cải tiến tự động thông qua kinh nghiệm và là trung tâm của các nghiên cứu về AI từ khi bắt đầu thực địa.', 'tri-tue-nhan-tao_1514777421.jpeg', '', '', null, null, '0', 'tri-tue-nhan-tao');
INSERT INTO `topics` VALUES ('114', 'Tâm lý Xã hội', null, '6', 'Nghiên cứu về hành vi của con người trong các nhóm xã hội hoặc xã hội, bao gồm cả cách nó bị ảnh hưởng bởi các thành viên được nhận thức trong các nhóm đó và nó ảnh hưởng như thế nào đến nh?', 'Nghiên cứu về hành vi của con người trong các nhóm xã hội hoặc xã hội, bao gồm cả cách nó bị ảnh hưởng bởi các thành viên được nhận thức trong các nhóm đó và nó ảnh hưởng như thế nào đến nhận thức của các thành viên của các nhóm khác. Không Tự Hoàn thiện hoặc Mẹo và Hacks cho Cuộc sống Hàng Ngày.', 'tam-ly-xa-hoi_1514777476.jpeg', '', '', null, null, '0', 'tam-ly-xa-hoi');
INSERT INTO `topics` VALUES ('115', 'Vũ trụ', null, '6', 'Vũ trụ thường được định nghĩa là:\r\n\"tất cả mọi thứ tồn tại\", bao gồm tất cả vật chất, năng lượng, thời gian, các thiên hà, và các nội dung của không gian giữa các thiên hà hoặc: trái đất cùn', 'Vũ trụ thường được định nghĩa là:\r\n\"tất cả mọi thứ tồn tại\", bao gồm tất cả vật chất, năng lượng, thời gian, các thiên hà, và các nội dung của không gian giữa các thiên hà hoặc: trái đất cùng với tất cả cư dân của nó và tạo ra mọi thứ và thời gian', 'vu-tru_1514777546.jpeg', '', '', null, null, '0', 'vu-tru');
INSERT INTO `topics` VALUES ('116', 'Thuật toán', null, '6', 'Thuật toán là một phương pháp được xác định chính xác, có hiệu quả và hữu hạn để tạo ra đầu ra xác định rõ ràng từ đầu vào được xác định rõ.', 'Thuật toán là một phương pháp được xác định chính xác, có hiệu quả và hữu hạn để tạo ra đầu ra xác định rõ ràng từ đầu vào được xác định rõ.', 'thuat-toan_1514777591.jpeg', '', '', null, null, '0', 'thuat-toan');
INSERT INTO `topics` VALUES ('117', 'Thần kinh học', null, '6', 'Khoa học thần kinh là nghiên cứu khoa học về hệ thần kinh. Theo truyền thống, khoa học thần kinh đã được xem như là một chi nhánh của sinh học, nhưng nó đã phát triển để bao gồm một phạm vi rộng cá', 'Khoa học thần kinh là nghiên cứu khoa học về hệ thần kinh. Theo truyền thống, khoa học thần kinh đã được xem như là một chi nhánh của sinh học, nhưng nó đã phát triển để bao gồm một phạm vi rộng các lĩnh vực liên ngành làm việc cùng nhau để làm sáng tỏ chức năng não ở nhiều mức độ điều tra.', 'than-kinh-hoc_1514777677.jpeg', '', '', null, null, '0', 'than-kinh-hoc');
INSERT INTO `topics` VALUES ('118', 'Ngôn ngữ lập trình', null, '6', 'Chủ đề này là dành cho các câu hỏi về ngôn ngữ lập trình nói chung. Nó bao gồm các so sánh các ngôn ngữ, lựa chọn ngôn ngữ, thiết kế ngôn ngữ, lý thuyết và lịch sử. Nó không bao gồm các câu hỏi v', 'Chủ đề này là dành cho các câu hỏi về ngôn ngữ lập trình nói chung. Nó bao gồm các so sánh các ngôn ngữ, lựa chọn ngôn ngữ, thiết kế ngôn ngữ, lý thuyết và lịch sử. Nó không bao gồm các câu hỏi về ngôn ngữ lập trình cụ thể-những người nên đi theo chủ đề cho ngôn ngữ cụ thể.', 'ngon-ngu-lap-trinh_1514777731.jpeg', '', '', null, null, '0', 'ngon-ngu-lap-trinh');
INSERT INTO `topics` VALUES ('119', 'Thống kê (kỷ luật học thuật) ', null, '6', 'Các chi nhánh của toán học đối phó với việc thu thập, phân tích, giải thích, và trình bày các dữ liệu định lượng.', 'Các chi nhánh của toán học đối phó với việc thu thập, phân tích, giải thích, và trình bày các dữ liệu định lượng.', 'thong-ke-ky-luat-hoc-thuat_1514777812.jpeg', '', '', null, null, '0', 'thong-ke-ky-luat-hoc-thuat');
INSERT INTO `topics` VALUES ('120', 'Xác suất (thống kê)', null, '6', 'Khoa học toán học về cơ hội và tính đúng đắn. Đối với Câu hỏi thường gặp về chủ đề, hãy kiểm tra: Câu hỏi thường gặp về xác suất (thống kê) là gì?', 'Khoa học toán học về cơ hội và tính đúng đắn. Đối với Câu hỏi thường gặp về chủ đề, hãy kiểm tra: Câu hỏi thường gặp về xác suất (thống kê) là gì?', 'xac-suat-thong-ke_1514777890.jpeg', '', '', null, null, '0', 'xac-suat-thong-ke');
INSERT INTO `topics` VALUES ('121', 'Thiết kế', null, '10', 'Thiết kế có thể là một danh từ hoặc động từ. Thiết kế là bản fax của một đối tượng không tồn tại.', 'Thiết kế có thể là một danh từ hoặc động từ. Thiết kế là bản fax của một đối tượng không tồn tại.', 'thiet-ke_1514779277.jpeg', '', '', null, null, '0', 'thiet-ke');
