INSERT INTO `topics` VALUES ('123', 'Thời trang và Phong cách', null, '10', 'Thời trang đề cập đến phong cách hiện hành của quần áo và phụ kiện (giày dép, túi xách, vv) được sản xuất và sử dụng trong một khoảng thời gian cụ thể.\r\nPhong cách đề cập đến một biểu hiện ', 'Thời trang đề cập đến phong cách hiện hành của quần áo và phụ kiện (giày dép, túi xách, vv) được sản xuất và sử dụng trong một khoảng thời gian cụ thể.\r\nPhong cách đề cập đến một biểu hiện đặc biệt của các yếu tố trong một khu vực chủ đề cụ thể. Trong bối cảnh thời trang, nó đề cập đến chế độ ăn mặc đặc trưng của một người.\r\nCả hai thuật ngữ này thường được sử dụng cho nhau.', 'thoi-trang-va-phong-cach_1514779420.jpeg', '', '', null, null, '0', 'thoi-trang-va-phong-cach');
INSERT INTO `topics` VALUES ('124', 'Mỹ Thuật', null, '10', 'Nghệ thuật đánh giá cao về nội dung trí tưởng tượng, thẩm mỹ, trí tuệ của nó, thường là nghệ thuật thị giác', 'Nghệ thuật đánh giá cao về nội dung trí tưởng tượng, thẩm mỹ, trí tuệ của nó, thường là nghệ thuật thị giác', 'my-thuat_1514779465.jpeg', '', '', null, null, '0', 'my-thuat');
INSERT INTO `topics` VALUES ('125', 'Thiết kế Web', null, '10', 'Thực tiễn thiết kế được áp dụng cho các trang web và trang web, bao gồm các mẫu cho blog và các trang web điều khiển hệ thống quản lý nội dung.', 'Thực tiễn thiết kế được áp dụng cho các trang web và trang web, bao gồm các mẫu cho blog và các trang web điều khiển hệ thống quản lý nội dung.', 'thiet-ke-web_1514779516.jpeg', '', '', null, null, '0', 'thiet-ke-web');
INSERT INTO `topics` VALUES ('126', 'Giao diện người dùng', null, '10', '- Giao diện người dùng, trong lĩnh vực thiết kế công nghiệp tương tác giữa người và máy, là không gian nơi các tương tác giữa con người và máy móc xảy ra.\r\n- Màn hình cảm ứng và bàn phím là các giao di', '- Giao diện người dùng, trong lĩnh vực thiết kế công nghiệp tương tác giữa người và máy, là không gian nơi các tương tác giữa con người và máy móc xảy ra.\r\n- Màn hình cảm ứng và bàn phím là các giao diện người dùng phổ biến nhất.', 'giao-dien-nguoi-dung_1514779616.jpeg', '', '', null, null, '0', 'giao-dien-nguoi-dung');
INSERT INTO `topics` VALUES ('127', 'Nhiếp ảnh kỹ thuật số', null, '10', 'Một chủ đề phụ của Nhiếp ảnh, trong đó các vấn đề kỹ thuật số đặc biệt là trọng tâm. Định dạng tệp tin, thao tác hình ảnh số, thẻ nhớ hoặc lưu trữ kỹ thuật số.\r\nBất cứ điều gì liên', 'Một chủ đề phụ của Nhiếp ảnh, trong đó các vấn đề kỹ thuật số đặc biệt là trọng tâm. Định dạng tệp tin, thao tác hình ảnh số, thẻ nhớ hoặc lưu trữ kỹ thuật số.\r\nBất cứ điều gì liên quan đến tính chất cụ thể của việc sử dụng cảm biến hoặc quá trình xử lý hình ảnh để tạo ra hình ảnh.', 'nhiep-anh-ky-thuat-so_1514779692.jpeg', '', '', null, null, '0', 'nhiep-anh-ky-thuat-so');
INSERT INTO `topics` VALUES ('128', 'Thiết kế sản phẩm hàng hoá hiện vật', null, '10', 'Việc thiết kế các sản phẩm có thể tiếp tục tồn tại ngay cả khi internet biến mất.', 'Việc thiết kế các sản phẩm có thể tiếp tục tồn tại ngay cả khi internet biến mất.', 'thiet-ke-san-pham-hang-hoa-hien-vat_1514779749.jpeg', '', '', null, null, '0', 'thiet-ke-san-pham-hang-hoa-hien-vat');
INSERT INTO `topics` VALUES ('129', 'Quần áo và Trang phục', null, '10', 'Quần áo là chất xơ và vật liệu dệt đeo trên cơ thể. Việc mặc quần áo chủ yếu là hạn chế đối với con người và là một tính năng của gần như tất cả các xã hội loài người. Số lượng và lo?', 'Quần áo là chất xơ và vật liệu dệt đeo trên cơ thể. Việc mặc quần áo chủ yếu là hạn chế đối với con người và là một tính năng của gần như tất cả các xã hội loài người. Số lượng và loại quần áo mòn phụ thuộc vào các cân nhắc về thể chất, xã hội và địa lý. Đó là chức năng chính là để phục vụ như là một phương tiện của cả sự thoải mái và bảo vệ khỏi các yếu tố môi trường. Đó là thông qua là một xã hội hiện đại, cộng đồng và chấp nhận pháp luật bình thường. Morden', 'quan-ao-va-trang-phuc_1514779795.jpeg', '', '', null, null, '0', 'quan-ao-va-trang-phuc');
INSERT INTO `topics` VALUES ('130', 'Báo chí', null, '14', 'Hành động tạo ra những lời kể có ý nghĩa sau khi thu thập, lọc và phân phối thông tin từ các nguồn khác nhau một cách kịp thời, hùng hồn, chính xác, đáng tin cậy, độc lập và có lợi cho xã hội.', 'Hành động tạo ra những lời kể có ý nghĩa sau khi thu thập, lọc và phân phối thông tin từ các nguồn khác nhau một cách kịp thời, hùng hồn, chính xác, đáng tin cậy, độc lập và có lợi cho xã hội.', 'bao-chi_1514865461.jpeg', '', '', null, null, '0', 'bao-chi');
INSERT INTO `topics` VALUES ('131', 'Giải trí', null, '14', 'Giải trí là một hoạt động, sự kiện hoặc hoạt động nhằm mục đích giải trí, vui chơi và quan tâm đến công chúng. Đối tượng này có thể có vai trò thụ động, như trong trường hợp một vở kịch, ', 'Giải trí là một hoạt động, sự kiện hoặc hoạt động nhằm mục đích giải trí, vui chơi và quan tâm đến công chúng. Đối tượng này có thể có vai trò thụ động, như trong trường hợp một vở kịch, một chương trình trên TV hoặc một bộ phim, hoặc đang hoạt động, như trong trường hợp trò chơi điện tử. Hình thức hoạt động tích cực của vui chơi, chẳng hạn như thể thao, thường được coi là giải trí.', 'giai-tri_1514865539.jpeg', '', '', null, null, '0', 'giai-tri');
INSERT INTO `topics` VALUES ('132', 'Phim', null, '14', 'Một chủ đề lớn bao gồm các kết quả của sản xuất phim. Trong khi nhiều chủ đề phụ cụ thể (như Phim kinh dị và Phim Hành động) tồn tại, đây là chiếc dù lớn để nói về bất kỳ bộ phim điện ?', 'Một chủ đề lớn bao gồm các kết quả của sản xuất phim. Trong khi nhiều chủ đề phụ cụ thể (như Phim kinh dị và Phim Hành động) tồn tại, đây là chiếc dù lớn để nói về bất kỳ bộ phim điện ảnh nào được trình chiếu trên sân khấu trước đó.\r\nNếu bạn có câu hỏi về quá trình làm phim và những người làm như vậy, vui lòng thử nhiều chủ đề cụ thể hơn như Đạo diễn phim và Điện ảnh.', 'phim_1514865616.jpeg', '', '', null, null, '0', 'phim');
INSERT INTO `topics` VALUES ('133', 'Âm nhạc', null, '14', 'Nghệ thuật kết hợp giữa âm thanh và sự im lặng và đôi khi từ ngữ và bài trừu tượng để tạo ra một trải nghiệm chủ quan về vẻ đẹp, thường liên quan đến biểu hiện cảm xúc.', 'Nghệ thuật kết hợp giữa âm thanh và sự im lặng và đôi khi từ ngữ và bài trừu tượng để tạo ra một trải nghiệm chủ quan về vẻ đẹp, thường liên quan đến biểu hiện cảm xúc.', 'am-nhac_1514865669.jpeg', '', '', null, null, '0', 'am-nhac');
