INSERT INTO `topics` VALUES ('135', 'Truyền hình', null, '14', 'Một tivi (cũng có tivi, truyền hình hay ống) là một máy có màn hình. Tivi được phát tín hiệu và biến chúng thành hình ảnh và âm thanh.', 'Một tivi (cũng có tivi, truyền hình hay ống) là một máy có màn hình. Tivi được phát tín hiệu và biến chúng thành hình ảnh và âm thanh.', 'truyen-hinh_1514865783.jpeg', '', '', null, null, '0', 'truyen-hinh');
