INSERT INTO `topics` VALUES ('137', 'Thực phẩm', null, '17', 'Thực phẩm là về các thành phần mà đi vào Nấu ăn, có lẽ theo Recipes. Thực phẩm là chất cung cấp dinh dưỡng cho sinh vật sống (con người, động vật hoặc thực vật).', 'Thực phẩm là về các thành phần mà đi vào Nấu ăn, có lẽ theo Recipes. Thực phẩm là chất cung cấp dinh dưỡng cho sinh vật sống (con người, động vật hoặc thực vật).', 'thuc-pham_1514865947.jpeg', '', '', null, null, '0', 'thuc-pham');
INSERT INTO `topics` VALUES ('138', 'Nấu ăn', null, '17', 'Nấu nướng - hành động áp dụng nhiệt, lạnh, axit, hoặc kiềm vào các phần ăn được và ăn được, giải quyết hương vị và tạo ra một sản phẩm ngon miệng trong quá trình.', 'Nấu nướng - hành động áp dụng nhiệt, lạnh, axit, hoặc kiềm vào các phần ăn được và ăn được, giải quyết hương vị và tạo ra một sản phẩm ngon miệng trong quá trình.', 'nau-an_1514866082.jpeg', '', '', null, null, '0', 'nau-an');
INSERT INTO `topics` VALUES ('139', 'Công thức nấu ăn', null, '17', 'Công thức nấu ăn là hướng dẫn để Nấu ăn hoặc Chuẩn bị Thực phẩm. Các công thức nấu ăn thường bao gồm các đặc điểm của thành phần (phẩm chất và số lượng), các quy trình phải tuân theo, thi?', 'Công thức nấu ăn là hướng dẫn để Nấu ăn hoặc Chuẩn bị Thực phẩm. Các công thức nấu ăn thường bao gồm các đặc điểm của thành phần (phẩm chất và số lượng), các quy trình phải tuân theo, thiết bị được sử dụng, nhiệt độ và thời gian nấu.', 'cong-thuc-nau-an_1514866306.jpeg', '', '', null, null, '0', 'cong-thuc-nau-an');
INSERT INTO `topics` VALUES ('140', 'Nhà hàng', null, '17', 'Các cơ sở phục vụ thức ăn và đồ uống cho khách hàng để đổi lấy tiền.', 'Các cơ sở phục vụ thức ăn và đồ uống cho khách hàng để đổi lấy tiền.', 'nha-hang_1514866400.jpeg', '', '', null, null, '0', 'nha-hang');
INSERT INTO `topics` VALUES ('141', 'Khởi nghiệp ( Startups )', null, '7', 'Khởi động là một tổ chức được thành lập để tìm kiếm một mô hình kinh doanh có thể lặp lại và có thể mở rộng. Các sản phẩm khởi nghiệp thường có xu hướng tập trung vào sản phẩm và đặc t', 'Khởi động là một tổ chức được thành lập để tìm kiếm một mô hình kinh doanh có thể lặp lại và có thể mở rộng. Các sản phẩm khởi nghiệp thường có xu hướng tập trung vào sản phẩm và đặc trưng bởi sự đổi mới.', 'khoi-nghiep-startups_1514868133.jpeg', '', '', null, null, '0', 'khoi-nghiep-startups');
INSERT INTO `topics` VALUES ('142', 'Bắt đầu khởi nghiệp', null, '7', 'Xem Tóm tắt Đánh giá Kinh doanh Harvard của Lean Startup ở đây: Tại sao sự thay đổi của Lean thay đổi Tất cả mọi thứ để tìm hiểu các khái niệm khởi động lean, bấm vào đây: Các Lean Startup, Eric Ries \'O', 'Xem Tóm tắt Đánh giá Kinh doanh Harvard của Lean Startup ở đây: Tại sao sự thay đổi của Lean thay đổi Tất cả mọi thứ để tìm hiểu các khái niệm khởi động lean, bấm vào đây: Các Lean Startup, Eric Ries \'Online Course', 'bat-dau-khoi-nghiep_1514869249.jpeg', '', '', null, null, '0', 'bat-dau-khoi-nghiep');
INSERT INTO `topics` VALUES ('143', 'Người sáng lập và doanh nghiệp', null, '7', 'Một người thiết lập một doanh nghiệp hoặc doanh nghiệp, chịu rủi ro tài chính với hy vọng lợi nhuận.', 'Một người thiết lập một doanh nghiệp hoặc doanh nghiệp, chịu rủi ro tài chính với hy vọng lợi nhuận.', 'nguoi-sang-lap-va-doanh-nghiep_1514869391.jpeg', '', '', null, null, '0', 'nguoi-sang-lap-va-doanh-nghiep');
INSERT INTO `topics` VALUES ('144', 'Chiến lược khởi nghiệp', null, '7', 'Chiến lược khởi động là về các nguyên tắc chung cần thiết để khởi động thành công. Điều này khác với Lời khuyên khởi động là về lời khuyên cho các tình huống khởi động cụ thể.', 'Chiến lược khởi động là về các nguyên tắc chung cần thiết để khởi động thành công. Điều này khác với Lời khuyên khởi động là về lời khuyên cho các tình huống khởi động cụ thể.', 'chien-luoc-khoi-nghiep_1514869485.jpeg', '', '', null, null, '0', 'chien-luoc-khoi-nghiep');
INSERT INTO `topics` VALUES ('145', 'Vốn đầu tư mạo hiểm', null, '7', 'Vốn đầu tư mạo hiểm (còn gọi là VC hay Venture) là một loại vốn cổ phần tư nhân thường cung cấp cho các công ty có tiềm năng tăng trưởng giai đoạn đầu, có tiềm năng tạo ra lợi nhuận thông qua mộ', 'Vốn đầu tư mạo hiểm (còn gọi là VC hay Venture) là một loại vốn cổ phần tư nhân thường cung cấp cho các công ty có tiềm năng tăng trưởng giai đoạn đầu, có tiềm năng tạo ra lợi nhuận thông qua một sự kiện thực hiện cuối cùng như IPO hoặc bán thương mại công ty.', 'von-dau-tu-mao-hiem_1514869551.jpeg', '', '', null, null, '0', 'von-dau-tu-mao-hiem');
INSERT INTO `topics` VALUES ('146', 'Kinh doanh', null, '8', 'Một tổ chức được tạo ra với mục đích cung cấp hàng hoá và / hoặc dịch vụ cho các thực thể có thể có lợi từ chúng.', 'Một tổ chức được tạo ra với mục đích cung cấp hàng hoá và / hoặc dịch vụ cho các thực thể có thể có lợi từ chúng.', 'kinh-doanh_1514869906.jpeg', '', '', null, null, '0', 'kinh-doanh');
INSERT INTO `topics` VALUES ('147', 'Doanh nhân', null, '8', 'Doanh nhân là hành động xác định cơ hội và có nguy cơ biến chúng thành hiện thực.', 'Doanh nhân là hành động xác định cơ hội và có nguy cơ biến chúng thành hiện thực.', 'doanh-nhan_1514869987.jpeg', '', '', null, null, '0', 'doanh-nhan');
INSERT INTO `topics` VALUES ('148', 'Tài chính', null, '8', 'Tài chính là một chi nhánh của nền kinh tế nghiên cứu việc quản lý tiền trong việc tiết kiệm, chi tiêu, cho vay và ngân sách. Thuật ngữ này thường được sử dụng để mô tả hoạt động thương mại tr', 'Tài chính là một chi nhánh của nền kinh tế nghiên cứu việc quản lý tiền trong việc tiết kiệm, chi tiêu, cho vay và ngân sách. Thuật ngữ này thường được sử dụng để mô tả hoạt động thương mại trong việc cung cấp và sắp xếp quỹ cho ba thị trường rộng lớn về kinh doanh, cá nhân và chính phủ.', 'tai-chinh_1514870055.jpeg', '', '', null, null, '0', 'tai-chinh');
INSERT INTO `topics` VALUES ('149', 'Chiến lược kinh doanh', null, '8', 'Chiến lược kinh doanh là một khía cạnh của kế hoạch kinh doanh nhằm cố gắng sắp xếp và sắp xếp các nguồn lực sao cho có được lợi thế cạnh tranh liên quan đến các sự kiện tương lai đã được nh', 'Chiến lược kinh doanh là một khía cạnh của kế hoạch kinh doanh nhằm cố gắng sắp xếp và sắp xếp các nguồn lực sao cho có được lợi thế cạnh tranh liên quan đến các sự kiện tương lai đã được nhận biết hoặc các xu hướng đang phát triển và vị trí của hoạt động kinh doanh với người khác.', 'chien-luoc-kinh-doanh_1514870119.jpeg', '', '', null, null, '0', 'chien-luoc-kinh-doanh');
INSERT INTO `topics` VALUES ('150', 'Tiếp thị', null, '8', 'Tiếp thị là hoạt động, tập hợp các tổ chức, và các quá trình tạo ra, giao tiếp, phân phối và trao đổi các dịch vụ có giá trị cho khách hàng, khách hàng, đối tác và xã hội nói chung.', 'Tiếp thị là hoạt động, tập hợp các tổ chức, và các quá trình tạo ra, giao tiếp, phân phối và trao đổi các dịch vụ có giá trị cho khách hàng, khách hàng, đối tác và xã hội nói chung.', 'tiep-thi_1514870183.jpeg', '', '', null, null, '0', 'tiep-thi');
INSERT INTO `topics` VALUES ('151', 'Đầu tư', null, '8', 'Thực hiện cam kết (tiền hoặc vốn) để thu được lợi nhuận tài chính.', 'Thực hiện cam kết (tiền hoặc vốn) để thu được lợi nhuận tài chính.', 'dau-tu_1514870242.jpeg', '', '', null, null, '0', 'dau-tu');
INSERT INTO `topics` VALUES ('152', 'Mô hình kinh doanh ', null, '8', 'Một mô hình kinh doanh là một phần của chiến lược kinh doanh của một liên doanh. Mô hình kinh doanh giải thích cách thức liên doanh sẽ phối hợp các chức năng của doanh nghiệp để tạo ra, cung cấp và nắm', 'Một mô hình kinh doanh là một phần của chiến lược kinh doanh của một liên doanh. Mô hình kinh doanh giải thích cách thức liên doanh sẽ phối hợp các chức năng của doanh nghiệp để tạo ra, cung cấp và nắm bắt giá trị theo cách tạo thu nhập bền vững.', 'mo-hinh-kinh-doanh_1514870289.jpeg', '', '', null, null, '0', 'mo-hinh-kinh-doanh');
INSERT INTO `topics` VALUES ('153', 'Thị trường chứng khoán', null, '8', 'Một thị trường chứng khoán hoặc thị trường chứng khoán là một thực thể công cộng (một mạng lưới lỏng lẻo của các giao dịch kinh tế, không phải là một cơ sở vật chất hoặc một thực thể r??', 'Một thị trường chứng khoán hoặc thị trường chứng khoán là một thực thể công cộng (một mạng lưới lỏng lẻo của các giao dịch kinh tế, không phải là một cơ sở vật chất hoặc một thực thể rời rạc) cho việc kinh doanh cổ phiếu công ty và các công cụ phái sinh với giá thỏa thuận; đây là những chứng khoán được niêm yết trên sàn chứng khoán cũng như các chứng khoán được giao dịch riêng lẻ.', 'thi-truong-chung-khoan_1514870394.jpeg', '', '', null, null, '0', 'thi-truong-chung-khoan');
INSERT INTO `topics` VALUES ('154', 'Tư vấn nghề nghiệp', null, '8', 'Chủ đề này tổng hợp tất cả các nội dung liên quan đến truy vấn nghề nghiệp của bạn', 'Chủ đề này tổng hợp tất cả các nội dung liên quan đến truy vấn nghề nghiệp của bạn', 'tu-van-nghe-nghiep_1514870514.jpeg', '', '', null, null, '0', 'tu-van-nghe-nghiep');
INSERT INTO `topics` VALUES ('155', 'Tài chính cá nhân', null, '8', 'Tài chính cá nhân là việc áp dụng các nguyên tắc về tài chính vào các quyết định tiền tệ của một cá nhân hoặc một đơn vị gia đình. Nó giải quyết các cách thức mà các cá nhân hoặc gia đình có ?', 'Tài chính cá nhân là việc áp dụng các nguyên tắc về tài chính vào các quyết định tiền tệ của một cá nhân hoặc một đơn vị gia đình. Nó giải quyết các cách thức mà các cá nhân hoặc gia đình có được, ngân sách, tiết kiệm và chi tiêu các nguồn tài chính theo thời gian, có tính đến các rủi ro tài chính khác nhau và các sự kiện trong tương lai của cuộc sống. Các thành phần của tài chính cá nhân có thể bao gồm kiểm tra và tài khoản tiết kiệm, thẻ tín dụng và cho vay tiêu dùng, đầu tư vào thị trường chứng khoán, kế hoạch nghỉ hưu, phúc lợi xã hội, chính sách bảo hiểm và quản lý thuế thu nhập. ( nguồn Wikipedia)', 'tai-chinh-ca-nhan_1514870838.jpeg', '', '', null, null, '0', 'tai-chinh-ca-nhan');
