INSERT INTO `topics` VALUES ('157', 'Tham quan và Du lịch', null, '12', 'Để tránh xa môi trường làm việc của mình, theo một nghĩa nào đó, thoát khỏi cái tôi của mình; và điều này thường là lợi thế chính của du lịch và thay đổi.', 'Để tránh xa môi trường làm việc của mình, theo một nghĩa nào đó, thoát khỏi cái tôi của mình; và điều này thường là lợi thế chính của du lịch và thay đổi.', 'tham-quan-va-du-lich_1514871149.jpeg', '', '', null, null, '0', 'tham-quan-va-du-lich');
