INSERT INTO `topics` VALUES ('160', 'Tổ chức du lịch', null, '12', 'Du lịch là đi du lịch cho các mục đích vui chơi giải trí.\r\nChủ đề này đề cập đến việc chia sẻ, gợi ý và giúp đỡ những người có ý tưởng, địa điểm, ý kiến ​​vv, về các điểm đến du lị', 'Du lịch là đi du lịch cho các mục đích vui chơi giải trí.\r\nChủ đề này đề cập đến việc chia sẻ, gợi ý và giúp đỡ những người có ý tưởng, địa điểm, ý kiến ​​vv, về các điểm đến du lịch trên khắp thế giới.', 'to-chuc-du-lich_1514871348.jpeg', '', '', null, null, '0', 'to-chuc-du-lich');
