INSERT INTO `topics` VALUES ('170', 'Hẹn hò và mối quan hệ ', null, '15', 'Chủ đề này bao gồm các nỗ lực cốt lõi của con người tìm kiếm một người nào đó bạn có thể hạnh phúc với, giữ cho họ và bản thân hạnh phúc trong mối quan hệ, và làm thế nào để đối phó vớ', 'Chủ đề này bao gồm các nỗ lực cốt lõi của con người tìm kiếm một người nào đó bạn có thể hạnh phúc với, giữ cho họ và bản thân hạnh phúc trong mối quan hệ, và làm thế nào để đối phó với những vấn đề không thể tránh khỏi đó mọc lên khi mọi người gần gũi.', 'hen-ho-va-moi-quan-he_1514873646.jpeg', '', '', null, null, '0', 'hen-ho-va-moi-quan-he');
